mapper.select:
SELECT *
FROM (SELECT rownum rnum, a.*
	  FROM (SELECT * FROM course
	  		WHERE cos_category=#{cos_category}
	  		ORDER BY cos_num DESC)A)
WHERE rnum BETWEEN #{start} AND #{end}

SELECT *
FROM (SELECT ROWNUM RN, A.*
		FROM (SELECT * FROM course
			  ORDER BY cos_num DESC) A
		)
WHERE RN BETWEEN #{start} AND #{end}

mapper.update:
UPDATE course SET
cos_uploadfile = #{cos_uploadfile}, cos_filename = #{cos_filename},
cos_category = #{cos_category}, cos_title = #{cos_title}, cos_content = #{cos_content}, update_at = SYSDATE WHERE cos_num = #{cos_num}
